Webflow.push(function () {
  $("form").submit(function () {
    function e() {
      return true; // Always return true since we removed the validation
    }
    
    $(this).find(".error-message").remove();
    let s = 0;
    $(this)
      .find('[input-type="required"]')
      .each(function () {
        "" === $(this).val().trim()
          ? (s++,
            $(this).after('<span class="error-message">Required.</span>'),
            $(this).css("border-color", "red"))
          : $(this).css("border-color", "inherit");
      });
    
    let o = e(); // Call the modified function e()
    
    if (s > 0 || !o) // Removed the check for t
      return (
        $("html, body").animate(
          { scrollTop: $("#contact-form").offset().top },
          1e3
        ),
        !1
      );
  });
});
